package homework_5;import java.io.File;import java.util.HashMap;import java.util.Random;import java.util.Scanner;/** * This is a class to store the words for the use in the Hangman Game. * Functionality includes loading words from a file and returns a random not * used word when requested. * * @author Andrew Serra * @author Anindhya Kushagra */public class Dictionary {    int wordCount = 0;    String[] words = new String[1024];    HashMap<String, Boolean> wordUsage = new HashMap<>();    /**     * Reads in words to run the game with into an array.     *     * @param filename Name and path of the file to use for loading in the     *                 words.     */    public void loadWords(String filename) {        try {            Scanner sc = new Scanner(new File(filename));            while (sc.hasNextLine()) {                String word = sc.nextLine();                words[wordCount++] = word;                wordUsage.put(word, false);            }        } catch (Exception e) {            System.out.println("Something went wrong. Error: " + e.getMessage());            System.exit(1);        }    }    /**     * Using random numbers, selects a word not already used in the game.     *     * @return A string containing the word to play the game.     */    public String getNext() {        Random random = new Random();        int idx = random.nextInt(wordCount);        while(wordUsage.get(words[idx])) {            idx = random.nextInt(wordCount);        }        String nextWord = words[idx];        wordUsage.put(nextWord, true);        return nextWord;    }}